import QRCodeStyling from 'qr-code-styling';

export type DotType = 'rounded' | 'dots' | 'classy' | 'classy-rounded' | 'square' | 'extra-rounded';
export type CornerSquareType = 'dot' | 'square' | 'extra-rounded';
export type CornerDotType = 'dot' | 'square';

const brandIconMap: Record<string, string> = {
  'facebook': 'data:image/svg+xml;base64,',
  'instagram': 'data:image/svg+xml;base64,',
  'twitter-bird': 'data:image/svg+xml;base64,',
  'twitter-x': 'data:image/svg+xml;base64,',
  'twitter': 'data:image/svg+xml;base64,',
  'linkedin': 'data:image/svg+xml;base64,',
  'youtube': 'data:image/svg+xml;base64,',
  'paypal': 'data:image/svg+xml;base64,',
  'zoom': 'data:image/svg+xml;base64,',
  'wifi': 'data:image/svg+xml;base64,',
  'whatsapp': 'data:image/svg+xml;base64,',
  'download': 'data:image/svg+xml;base64,'
};

const brandColorMap: Record<string, string> = {
  'facebook': '#1877F2',
  'instagram': '#E4405F',
  'twitter-bird': '#1DA1F2',
  'twitter-x': '#000000',
  'twitter': '#1DA1F2',
  'linkedin': '#007cb7',
  'youtube': '#FF0000',
  'paypal': '#003087',
  'zoom': '#2D8CFF',
  'wifi': '#60A5FA',
  'whatsapp': '#25D366',
  'download': '#1E88E5'
};

function isValidImageUrl(url: string): boolean {
  if (!url) return false;
  if (url.startsWith('data:image/')) return true;
  if (url.startsWith('http://') || url.startsWith('https://')) return true;
  if (url.startsWith('/') || url.startsWith('./')) return true;
  return false;
}

export type QROptions = {
  data: string;
  size?: number;
  margin?: number;
  dotsType?: DotType;
  cornerSquareType?: CornerSquareType;
  cornerDotType?: CornerDotType;
  color?: {
    dark: string;
    light: string;
    innerEye?: string;
    outerEye?: string;
  };
  errorCorrectionLevel?: 'L' | 'M' | 'Q' | 'H';
  design?: {
    logo?: string;
    logoSize?: number;
    logoOpacity?: number;
    logoBackgroundColor?: string;
    removeLogoBackground?: boolean;
  };
};

export const generateQRCode = async (options: QROptions): Promise<string> => {
  const {
    data,
    size = 300,
    margin = 4,
    dotsType = 'square',
    cornerSquareType = 'square',
    cornerDotType = 'square',
    color = { dark: '#000000', light: '#ffffff' },
    errorCorrectionLevel = 'M',
    design
  } = options;

  return new Promise((resolve, reject) => {
    try {
      if (!data || !data.trim()) {
        reject(new Error('QR code data cannot be empty'));
        return;
      }

      let logoUrl = design?.logo;
      if (logoUrl && logoUrl !== 'none' && brandIconMap[logoUrl]) {
        logoUrl = brandIconMap[logoUrl];
      }

      const hasValidLogo = logoUrl && logoUrl !== 'none' && isValidImageUrl(logoUrl);
      
      const qrCodeOptions: any = {
        width: size,
        height: size,
        type: 'canvas',
        data: data.trim(),
        margin: margin,
        qrOptions: {
          typeNumber: 0,
          mode: 'Byte',
          errorCorrectionLevel: hasValidLogo ? 'H' : errorCorrectionLevel
        },
        dotsOptions: {
          color: color.dark,
          type: dotsType
        },
        backgroundOptions: {
          color: color.light,
        },
        cornersSquareOptions: {
          color: color.outerEye || color.dark,
          type: cornerSquareType
        },
        cornersDotOptions: {
          color: color.innerEye || color.dark,
          type: cornerDotType
        }
      };

      if (hasValidLogo) {
        qrCodeOptions.image = logoUrl;
        qrCodeOptions.imageOptions = {
          hideBackgroundDots: design?.removeBackground !== false,
          imageSize: (design.logoSize || 20) / 100,
          margin: 5,
          crossOrigin: 'anonymous',
        };
        
        if (design?.logoOpacity !== undefined && design.logoOpacity < 100) {
          qrCodeOptions.imageOptions.imageOptions = {
            opacity: design.logoOpacity / 100
          };
        }
      }

      const qrCode = new QRCodeStyling(qrCodeOptions);

      qrCode.getRawData('png').then(data => {
        if (!data) {
          reject(new Error('Failed to generate QR code: getRawData returned null'));
          return;
        }

        const blob = data instanceof Blob ? data : new Blob([data], { type: 'image/png' });
        const reader = new FileReader();
        reader.onloadend = () => {
          resolve(reader.result as string);
        };
        reader.onerror = () => {
          reject(new Error('Failed to read QR code data'));
        };
        reader.readAsDataURL(blob);
      }).catch(error => {
        reject(error);
      });

    } catch (error) {
      reject(error);
    }
  });
};

